console.log('Loaded ' + cards.length + ' cards');

exports.index = function(req, res){
  var list = [];

  for (var i=0; i<=8; i++) {
    list.push(cards[Math.floor((Math.random()*cards.length))]);
  }
  res.render('index', { title: 'TrekDeck', cards: list });
};

exports.view = function(req, res){
  var id = req.params.id;
  req.card = getCard(id);
  if (req.card) {
    res.render('cards/view', {
      title: req.card.title,
      card: req.card
    });
  } else {
    new Error('Card \'' + id + '\' not found');
  }
};

function getCard(id) {
  for (var i=0; i<cards.length; i++) {
    if (cards[i].id === id) {
      console.log('Card \'' + id + '\' at index ' + i);
      return cards[i];
    }
  }
  console.log('Card \'' + id + '\' not found');
  return undefined;
};
